import 'package:Test_Web/config/arguments_pass_file.dart';import 'package:Test_Web/config/theme.dart';import 'package:Test_Web/data/models/employee_list.dart';import 'package:Test_Web/home/EmployeeView.dart';import 'package:Test_Web/presentation/bloc.dart';import 'package:Test_Web/presentation/state.dart';import 'package:Test_Web/widgets/circle_progressbar.dart';import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:google_fonts/google_fonts.dart';class EmployeeScreen extends StatefulWidget {  @override  _EmployeeScreenState createState() => _EmployeeScreenState();}class _EmployeeScreenState extends State<EmployeeScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(        backgroundColor: Colors.white,        appBar: AppBar(          title: Text(            'Employee List ',            style: Theme.of(context).textTheme.subtitle1,          ),        ),        body: BlocBuilder<StartScreenBloc, StartScreenState>(            builder: (context, snapshot) {          print('state$snapshot');          if (snapshot is EmployeeLoadedState) {            return buildEmployeeList(snapshot.employeeScreen, context);          } else if (snapshot is EmployeeErrorState) {            return buildErrorUi(snapshot.message);          }          return showCircleProgress();        }));  }  Widget buildEmployeeList(      List<Employee> employeeScreen, BuildContext context) {    return SingleChildScrollView(        child: Column(      children: [        Padding(          padding: EdgeInsets.all(8),          child: InkWell(            onTap: () {              Navigator.pushNamed(context, "/search");            },            child: TextField(              enabled: false,              decoration: InputDecoration(hintText: 'Search employees'),              onChanged: (text) {},            ),          ),        ),        ListView.builder(          shrinkWrap: true,          itemCount: employeeScreen.length,          physics: NeverScrollableScrollPhysics(),          itemBuilder: (context, index) => InkWell(            onTap: () {              Navigator.pushNamed(context, '/employeeview',                  arguments: Employeeargs(                      employeeScreen[index].profileImage.toString(),                      employeeScreen[index].name,                      employeeScreen[index].username.toString(),                      employeeScreen[index].email,                      employeeScreen[index].address.street.toString(),                      employeeScreen[index].address.suite.toString(),                      employeeScreen[index].address.city.toString(),                      employeeScreen[index].phone.toString(),                      employeeScreen[index].website.toString()));            },            child: ListTile(              title: Text(                employeeScreen[index].name,                style: GoogleFonts.poppins(                  fontSize: 15,                  color: Colors.black,                ),              ),              subtitle: Text(                employeeScreen[index].email,                style: GoogleFonts.poppins(                  fontSize: 15,                  color: Colors.grey,                ),              ),              leading: Container(                width: 80.0,                height: 80.0,                decoration: new BoxDecoration(                  shape: BoxShape.circle,                  image: new DecorationImage(                    fit: BoxFit.cover,                    image: new CachedNetworkImageProvider(                        employeeScreen[index].profileImage),                  ),                ),              ),            ),          ),        ),      ],    ));  }  Widget buildErrorUi(String message) {    return Padding(      padding: const EdgeInsets.all(18.0),      child: Column(        children: [          SizedBox(            height: 10,          ),          Text(            "We couldn't connect to the page you are looking for.",            style: GoogleFonts.montserrat(                fontSize: 6,                color: AppColors.Blue,                fontWeight: FontWeight.w500),          ),        ],      ),    );  }}